INSERT INTO `cms_template` VALUES (7, 1630092239507464193, 'pc', 'footer.template.html', '<footer class=\"footer\">\n  <div class=\"container\">\n    <div class=\"row\">\n      <div class=\"col-lg-3 col-sm-3 address wow fadeInUp\" data-wow-duration=\"2s\" data-wow-delay=\".1s\">\n        <h1>联系我们</h1>\n        <address>\n          <p><i class=\"fa fa-home pr-10\"></i>地址: 深圳市宝安区福海街道福园一路35号天瑞工业园A3栋6楼东</p>\n          <p><i class=\"fa fa-globe pr-10\"></i>联系人：唐生</p>\n          <p><i class=\"fa fa-phone pr-10\"></i>电 话： 13128930981</p>\n          <p><i class=\"fa fa-phone pr-10\"></i>QQ： 407287334</p>\n          <p><i class=\"fa fa-envelope pr-10\"></i>Email: <a href=\"javascript:;\"\n              title=\"请把#替换成@\">haiguotang#swikoon.com</a></p>\n          <p><i class=\"fa fa-globe pr-10\"></i>网址：<a href=\"${Site.link}\">www.swikoon.com</a></p>\n        </address>\n      </div>\n      <div class=\"col-lg-3 col-sm-3 wow fadeInUp\" data-wow-duration=\"2s\" data-wow-delay=\".3s\">\n        <h1>最新动态</h1>\n        <div id=\"owl-slide\">\n          <@cms_content catalogalias=\"news\" level=\"CurrentAndChild\" page=\"false\" size=\"3\">\n            <#list DataList as content>\n              <div class=\"tweet-box\">\n                <a href=\"${content.link}\" style=\"color:#fff;\"> ${content.title} </a>\n              </div>\n            </#list>\n          </@cms_content>\n        </div>\n      </div>\n      <div class=\"col-lg-3 col-sm-3\">\n        <div class=\"page-footer wow fadeInUp\" data-wow-duration=\"2s\" data-wow-delay=\".5s\">\n          <h1>公司介绍</h1>\n          <ul class=\"page-footer-list\">\n            <@cms_catalog alias=\"company\" level=\"Child\" page=\"false\" size=\"5\">\n              <#list DataList as catalog>\n                <li><i class=\"fa fa-angle-right\"></i><a href=\"${catalog.link}\">${catalog.name}</a></li>\n              </#list>\n            </@cms_catalog>\n          </ul>\n        </div>\n      </div>\n      <div class=\"col-lg-3 col-sm-