   <div class=\"row\">\n        <div class=\"col-lg-4 col-sm-4\">\n          <h1>${Catalog.name}</h1>\n        </div>\n        <div class=\"col-lg-8 col-sm-8\" style=\"text-align:right;padding-top: 5px;\">\n          <a class=\"position\" href=\"${Site.link}\">首页</a>\n          <#list Catalog.ancestors?split(\":\") as catalogId>\n            <@cms_catalog id=\"${catalogId}\" level=\"Self\">\n              <#list DataList as catalog>\n                 > <a class=\"position\" href=\"${catalog.link}\">${catalog.name}</a>\n              </#list>\n            </@cms_catalog>\n          </#list>\n        </div>\n      </div>\n    </div>\n  </div>\n    <!--breadcrumbs end-->\n\n    <!--container start-->\n    <div class=\"container\">\n      <div class=\"row\">\n        <!--blog start-->\n        <div class=\"col-lg-9 \">\n          <p>${Catalog.link!}</p>\n          <p>List-${Catalog.listLink!}</p>\n          <@cms_content catalogid=\"${Catalog.catalogId}\" level=\"CurrentAndChild\" page=\"true\">\n          <#list DataList as content>\n          <div class=\"blog-item\">\n            <div class=\"row\">\n              <div class=\"col-lg-2 col-sm-2\">\n                <div class=\"date-wrap\">\n                  <span class=\"date\">\n                    ${dateFormat(content.publishDate,\"MM/dd\")}\n                  </span>\n                  <span class=\"month\">\n                    ${dateFormat(content.publishDate,\"yyyy\")}\n                  </span>\n                </div>\n\n              </div>\n              <div class=\"col-lg-10 col-sm-10\">\n                <h1 style=\"margin-top:0;\">\n                  <a href=\"${content.link}\">\n                    ${content.title}\n                  </a>\n                </h1>\n                <p>\n                	<#if content.summary?? && content.summary != \"\">\n	                ${content.summary}\n					<#else>\n					  <@cms_article contentId=\"${content.contentId}\">\n              <#assign cc = clearHtmlTag(ArticleContent)>\n                \n              <#if cc?length &lt; 320>${cc}<#else>${cc[0..300]}...</#if>\n					    \n					  </@cms_article>\n	                </#if>\n                </p>\n              </div>\n            </div>\n          </div>\n          </#list>\n          </@cms_content>\n        		<#if PageTotal &gt; 10>\n          <div class=\"text-center pagebar\">\n            <#if PageTotal==0>\n              <#assign PageCount=1 />\n              <#else>\n            <#assign PageCount=(PageTotal + PageSize - 1) / PageSize />\n              </#if>\n            <#if PageNo &gt; 1>\n              <a href=\"${OtherPage?replace(\'{0}\', PageNo-1)}\"> 上一页 </a>\n              </#if>\n            <#list 1..PageCount as pn>\n            <#if pn == 1>\n              <#if PageNo == pn><a href=\"javascript:void(0);\" class=\"current\">${pn}</a><#else><a href=\"${FirstPage}\">${pn}</a></#if>\n            <#else>\n              <#if PageNo == pn><a href=\"javascript:void(0);\" class=\"current\">${pn}</a><#else><a href=\"${OtherPage?replace(\'{0}\', pn)}\">${pn}</a></#if>\n            </#if>\n          </#list>\n            <#if PageNo &lt; PageCount?int>\n              <a href=\"${OtherPage?replace(\'{0}\', PageNo+1)}\"> 下一页 </a>\n              </#if>\n          </div>\n         </#if>\n        </div>\n\n        <div class=\"col-lg-3\">\n          <@cms_include file=\"rightblock.template.html\"></@cms_include>\n        </div>\n\n        <!--blog end-->\n      </div>\n\n    </div>\n    <!--container end-->\n\n    <!--footer start-->\n    <@cms_include file=\"footer.template.html\"></@cms_include>\n    <!--small footer end-->\n\n    <!-- js placed at the end of the document so the pages load faster -->\n    <script src=\"${Prefix}js/jquery.js\">\n    </script>\n    <script src=\"${Prefix}js/bootstrap.min.js\">\n    </script>\n    <script type=\"text/javascript\" src=\"${Prefix}js/hover-dropdown.js\">\n    </script>\n    <script defer src=\"${Prefix}js/jquery.flexslider.js\">\n    </script>\n    <script type=\"text/javascript\" src=\"${Prefix}assets/bxslider/jquery.bxslider.js\">\n    </script>\n\n    <script src=\"${Prefix}js/jquery.easing.min.js\">\n    </script>\n    <script src=\"${Prefix}js/link-hover.js\">\n    </script>\n\n\n    <!--common script for all pages-->\n    <script src=\"${Prefix}js/common-scripts.js\">\n    </script>\n\n\n    <script src=\"${Prefix}js/wow.min.js\">\n    </script>\n    <script>\n      wow = new WOW(\n        {\n          boxClass:     \'wow\',      // default\n          animateClass: \'animated\', // default\n          offset:       0          // default\n        }\n      )\n        wow.init();\n    </script>\n  </body>\n</html>', 6143, 1686023482263, '__system', '2023-03-09 19:22:13', 'admin', '2023-06-06 11:51:22', NULL);
INSERT INTO `cms_template` VALUES (12, 1630092239507464193, 'pc', 'list_product.template.html', '<!DOCTYPE html>\n<html lang=\"en\">\n  <head>\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n    <meta name=\"description\" content=\"Developed By M Abdur Rokib Promy\">\n    <meta name=\"author\" content=\"rokib\">\n    <meta name=\"keywords\" content=\"Bootstrap 3, Template, Theme, Responsive, Corporate, Business\">\n    <link rel=\"shortcut icon\" href=\"${Prefix}img/favicon.png\">\n\n    <title>${Catalog.name}_${Site.name}</title>\n\n    <!-- Bootstrap core CSS -->\n    <link href=\"${Prefix}css/bootstrap.min.css\" rel=\"stylesheet\">\n    <link href=\"${Prefix}css/theme.css\" rel=\"stylesheet\">\n    <link href=\"${Prefix}css/bootstrap-reset.css\" rel=\"stylesheet\">\n    <!--external css-->\n    <link href=\"${Prefix}assets/font-awesome/css/font-awesome.css\" rel=\"stylesheet\" />\n    <link rel=\"stylesheet\" href=\"${Prefix}css/flexslider.css\"/>\n    <link href=\"${Prefix}assets/bxslider/jquery.bxslider.css\" rel=\"stylesheet\" />\n    <link rel=\"stylesheet\" href=\"${Prefix}css/animate.css\">\n    <link href=\'http://fonts.googleapis.com/css?family=Lato\' rel=\'stylesheet\' type=\'text/css\'>\n    <link rel=\"stylesheet\" href=\"${Prefix}assets/owlcarousel/owl.carousel.css\">\n    <link rel=\"stylesheet\" type=\"text/css\" href=\"${Prefix}css/mixitup.css\">\n\n    <!-- Custom styles for this template -->\n    <link rel=\"stylesheet\" type=\"text/css\" href=\"${Prefix}css/component.css\">\n    <link href=\"${Prefix}css/style.css\" rel=\"stylesheet\">\n    <link href=\"${Prefix}css/style-responsive.css\" rel=\"stylesheet\" />\n    <link rel=\"stylesheet\" href=\"${Prefix}css/magnific-popup.css\">\n    <!-- HTML5 shim and Respond.js IE8 support of HTML5 tooltipss and media queries -->\n    <!--[if lt IE 9]>\n      <script src=\"${Prefix}js/html5shiv.js\"></script>\n      <script src=\"${Prefix}js/respond.min.js\"></script>\n    <![endif]-->\n    <style>div.companyinfo p, div.copyright p, address p { width:100%; }</style>\n  </head>\n\n  <body>\n    <!--header start-->\n    <@cms_include file=\"header.template.html\"></@cms_include>\n    <!--header end-->\n\n    <!--breadcrumbs start-->\n  <div class=\"breadcrumbs\">\n    <div class=\"container\">\n      <div class=\"row\">\n        <div class=\"col-lg-4 col-sm-4\">\n          <h1>${Catalog.name}</h1>\n        </div>\n        <div class=\"col-lg-8 col-sm-8\" style=\"text-align:right;padding-top: 5px;\">\n          <a class=\"position\" href=\"${Site.link}\">首页</a>\n          <#list Catalog.ancestors?split(\":\") as catalogId>\n            <@cms_catalog id=\"${catalogId}\" level=\"Self\">\n              <#list DataList as catalog>\n                 > <a class=\"position\" href=\"${catalog.link}\">${catalog.name}</a>\n              </#list>\n            </@cms_catalog>\n          </#list>\n        </div>\n      </div>\n    </div>\n  </div>\n    <!--breadcrumbs end-->\n\n\n    <div class=\"container\">\n      <div><a href=\"${Catalog.listLink}\">${Catalog.listLink}</a></div>\n        <div class=\"row\">\n        <div class=\"col-md-6\">\n            <ul id=\"filters\" class=\"clearfix\">\n                <li><span class=\"filter active\" data-filter=\"dk gk\">所有系列</span></li>\n                <li><span class=\"filter\" data-filter=\"dk\">DK系列</span></li>\n                <li><span class=\"filter\" data-filter=\"gk\">GK系列</span></li>\n            </ul>\n            </div>\n        </div>\n    </div>\n    <div class=\"container\">\n        <div class=\"row  mar-b-30\">\n        <div id=\"portfoliolist\">\n            <div class=\"col-md-12\">\n            <@cms_content catalogid=\"${Catalog.catalogId}\" level=\"CurrentAndChild\" page=\"true\" size=\"120\">\n            <#list DataList as content>\n            <div class=\"portfolio ${content.shortTitle!}\" data-cat=\"${content.shortTitle!}\">\n                <div class=\"portfolio-wrapper\">\n                	<a href=\"${content.link}\">\n                    <div class=\"portfolio-hover\">\n                        <div class=\"image-caption\">${content.title}</div>\n                      		<#if content.logo??>\n                        <img src=\"${imageSize(content.logo,240, 240)}\" alt=\"${content.title}\" />\n                        </#if>\n                    </div>\n                    </a>\n                </div>\n				<div style=\"background-color: #fff;text-align: center;padding-top: 5px;\">${content.title}</div>\n            </div>\n            </#list>\n            </@cms_content>\n            </div>\n        </div>\n       </div>\n    </div>\n    <!--footer start-->\n    <@cms_include file=\"footer.template.html\"></@cms_include>\n    <!--footer end-->\n\n    <!-- js placed at the end of the document so the pages load faster\n    <script src=\"${Prefix}js/jquery.js\"></script> -->\n    <script src=\"${Prefix}js/jquery-1.8.3.min.js\"></script>\n    <script src=\"${Prefix}js/bootstrap.min.js\"></script>\n    <script type=\"text/javascript\" src=\"${Prefix}js/hover-dropdown.js\"></script>\n    <script defer src=\"${Prefix}js/jquery.flexslider.js\"></script>\n    <script type=\"text/javascript\" src=\"${Prefix}assets/bxslider/jquery.bxslider.js\"></script>\n\n    <script type=\"text/javascript\" src=\"${Prefix}js/jquery.parallax-1.1.3.js\"></script>\n    <script src=\"${Prefix}js/wow.min.js\"></script>\n    <script src=\"${Prefix}assets/owlcarousel/owl.carousel.js\"></script>\n    <script src=\"${Prefix}js/mixitup.js\"></script>\n    <script src=\"${Prefix}js/jquery.easing.min.js\"></script>\n    <script src=\"${Prefix}js/link-hover.js\"></script>\n\n    <!--common script for all pages-->\n    <script src=\"${Prefix}js/common-scripts.js\"></script>\n\n    <script src=\"${Prefix}js/revulation-slide.js\"></script>\n    <script src=\"${Prefix}js/jquery.magnific-popup.js\"></script>\n\n    <script type=\"text/javascript\">\n    $(\'.image-caption a\').tooltip();\n    $(function () {\n\n        var filterList = {\n\n            init: function () {\n\n                // MixItUp plugin\n                // http://mixitup.io\n                $(\'#portfoliolist\').mixitup({\n                    targetSelector: \'.portfolio\',\n                    filterSelector: \'.filter\',\n                    effects: [\'fade\'],\n                    easing: \'snap\',\n                    // call the hover effect\n                    onMixEnd: filterList.hoverEffect()\n                });\n\n            },\n\n            hoverEffect: function () {\n                $(\"[rel=\'tooltip\']\").tooltip();\n                // Simple parallax effect\n                $(\'#portfoliolist .portfolio .portfolio-hover\').hover(\n        function(){\n            $(this).find(\'.image-caption\').slideDown(250); //.fadeIn(250)\n        },\n        function(){\n            $(this).find(\'.image-caption\').slideUp(250); //.fadeOut(205)\n        }\n    );\n\n\n\n            }\n\n        };\n\n        // Run the show!\n        filterList.init();\n\n\n    });\n\n    $( document ).ready(function() {\n\n        $(\'.magnefig\').each(function(){\n            $(this).magnificPopup({\n                type:\'image\',\n                removalDelay: 300,\n                mainClass: \'mfp-fade\'\n            })\n        });\n    });\n    </script>\n\n  <script>\n\n    $(document).ready(function() {\n\n        $(\"#owl-demo\").owlCarousel({\n\n            autoPlay: 3000, //Set AutoPlay to 3 seconds\n\n            items : 4,\n            itemsDesktop : [1199,3],\n            itemsDesktopSmall : [979,3],\n            stopOnHover : true,\n\n        });\n\n    });\n\n    new WOW().init();\n\n\n  </script>\n </body>\n</html>\n', 7179, 1684029617123, '__system', '2023-03-09 19:22:13', 'admin', '2023-05-14 10:00:17', NULL);
