INSERT INTO `cms_template` VALUES (14, 1630092239507464193, 'pc', 'detail_audio.template.html', '<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"utf-8\">\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\n  <meta name=\"description\" content=\"Developed By M Abdur Rokib Promy\">\n  <meta name=\"author\" content=\"cosmic\">\n  <meta name=\"keywords\" content=\"Bootstrap 3, Template, Theme, Responsive, Corporate, Business\">\n  <link rel=\"shortcut icon\" href=\"${Prefix}img/favicon.png\">\n  <title>${Content.title}_${Site.name}</title>\n\n  <!-- Bootstrap core CSS -->\n  <link href=\"${Prefix}css/bootstrap.min.css\" rel=\"stylesheet\">\n  <link href=\"${Prefix}css/theme.css\" rel=\"stylesheet\">\n  <link href=\"${Prefix}css/bootstrap-reset.css\" rel=\"stylesheet\">\n  <!--external css-->\n  <link href=\"${Prefix}assets/font-awesome/css/font-awesome.css\" rel=\"stylesheet\" />\n  <link rel=\"stylesheet\" href=\"${Prefix}css/flexslider.css\" />\n  <link href=\"${Prefix}assets/bxslider/jquery.bxslider.css\" rel=\"stylesheet\" />\n  <link rel=\"stylesheet\" href=\"${Prefix}css/animate.css\">\n  <link href=\'http://fonts.googleapis.com/css?family=Lato\' rel=\'stylesheet\' type=\'text/css\'>\n\n\n  <!-- Custom styles for this template -->\n  <link href=\"${Prefix}css/style.css\" rel=\"stylesheet\">\n  <link href=\"${Prefix}css/style-responsive.css\" rel=\"stylesheet\" />\n\n  <!-- HTML5 shim and Respond.js IE8 support of HTML5 tooltipss and media queries -->\n  <!--[if lt IE 9]>\n<script src=\"${Prefix}js/html5shiv.js\">\n</script>\n<script src=\"${Prefix}js/respond.min.js\">\n</script>\n<![endif]-->\n\n<style>\n.pagebar { height: 50px; }\n.pagebar a { border: 1px solid #48cfad; padding: 3px 8px; margin: 0 2px; }\n.pagebar a.current { background-color: #48cfad; color: #fff; }\n.article_container img { width: 100%; }\narticle_container. figure.image_resized {	margin: auto; }\n</style>\n</head>\n\n<body>\n  <!--header start-->\n  <@cms_include file=\"header.template.html\"></@cms_include>\n  <!--header end-->\n\n  <!--breadcrumbs start-->\n  <div class=\"breadcrumbs\">\n    <div class=\"container\">\n      <div class=\"row\">\n        <div class=\"col-lg-4 col-sm-4\">\n          <h1>${Catalog.name}</h1>\n        </div>\n        <div class=\"col-lg-8 col-sm-8\" style=\"text-align:right;padding-top: 5px;\">\n          <a class=\"position\" href=\"${Site.link}\">首页</a>\n          <#list Catalog.ancestors?split(\":\") as catalogId>\n            <@cms_catalog id=\"${catalogId}\" level=\"Self\">\n              <#list DataList as catalog>\n                 > <a class=\"position\" href=\"${catalog.link}\">${catalog.name}</a>\n              </#list>\n            </@cms_catalog>\n          </#list>\n        </div>\n      </div>\n    </div>\n  </div>\n  <!--breadcrumbs end-->\n\n  <!--container start-->\n  <div class=\"container\">\n    <div class=\"row\">\n      <!--blog start-->\n      <div class=\"col-lg-9\">\n        <div class=\"blog-item\">\n          <div class=\"row\">\n            <div class=\"col-lg-2 col-sm-2\">\n              <div class=\"date-wrap\">\n                <span class=\"date\">\n                  ${dateFormat(Content.publishDate, \'MM/dd\')}\n                </span>\n                <span class=\"month\">\n                  ${dateFormat(Content.publishDate, \'yyyy\')}\n                </span>\n              </div>\n              <div class=\"author\">\n                By\n                <a href=\"#\">\n                  ${Content.author!Site.name}\n                </a>\n              </div>\n              <div class=\"st-view\">\n                <ul class=\"list-unstyled\">\n                  <li>\n                    <a href=\"javascript:;\">\n                      ${randomInt(10000)}&nbsp;看过\n                    </a>\n                  </li>\n                  <li>\n                    <a href=\"javascript:;\">\n                      ${randomInt(20)}&nbsp;分享过\n                    </a>\n                  </li>\n                  <!-- <li>\n                      <a href=\"javascript:;\">\n                        15&nbsp;评论\n                      </a>\n                    </li> -->\n                </ul>\n              </div>\n            </div>\n            <div class=\"col-lg-10 col-sm-10\">\n              <h1 style=\"margin-top:0;\">\n                <a href=\"javascript:void(0);\">\n                  ${Content.title}\n                </a>\n              </h1>\n              <div class=\"article_container\">\n                <@cms_audio contentid=\"${Content.contentId}\" page=\"false\">\n                  <#list DataList as audio>\n                    <h2>音频标题：${audio.title}</h2>\n                    <p>音频时长：${audio.duration} ms</p>\n                    <p>音频格式：${audio.format}</p>\n                    <p>音频地址：${internalUrl(audio.path)}</p>\n                		</#list>\n                </@cms_audio>\n              </div>\n            </div>\n          </div>\n        </div>\n      </div>\n\n      <div class=\"col-lg-3\">\n        <div class=\"blog-side-item\">\n          <div class=\"search-row\">\n            <input type=\"text\" class=\"form-control\" placeholder=\"Search here\">\n          </div>\n          <#if Catalog.parentId!=0>\n          <@cms_catalog id=\"${Catalog.ancestors[0..(Catalog.ancestors?index_of(\':\')-1)]}\" level=\"Child\" size=\"10\">\n            <#if Total?? && Total &gt; 0>\n              <div class=\"category\">\n                <h3>\n                  ${Catalog.name}\n                </h3>\n                <ul class=\"list-unstyled\">\n                  <#list DataList as child>\n                    <li>\n                      <a href=\"${child.link}\">\n                        <i class=\"fa fa-angle-right pr-10\">\n                        </i>\n                        ${child.name}\n                      </a>\n                    </li>\n                  </#list>\n                </ul>\n              </div>\n 